import { createElement, RenderImage, Size } from '@canvas-ui/core'
import type { Story } from '@storybook/react'
import React, { useEffect, useRef } from 'react'

export const RenderImageTest: Story = () => {

  const canvasElRef = useRef<HTMLCanvasElement | null>(null)

  useEffect(() => {
    // 等待布局稳定
    setTimeout(() => {
      if (!canvasElRef.current) {
        return
      }

      const canvasRect = canvasElRef.current.getBoundingClientRect()
      const surfaceSize = Size.fromWH(canvasRect.width, canvasRect.height)

      const canvas = createElement('Canvas')
      canvas.prepareInitialFrame()
      canvas.el = canvasElRef.current
      canvas.size = surfaceSize
      canvas.dpr = devicePixelRatio

      function addCat(src: string, crossorigin?: RenderImage['crossOrigin'], width?: number, height?: number) {
        const el = createElement('Image')
        el.crossOrigin = crossorigin ?? null
        el.src = src
        el.style.width = width
        el.style.height = height
        el.style.marginLeft = 4
        el.style.marginTop = 4
        el.style.marginRight = 4
        el.style.marginBottom = 4
        el.style.boxShadow = '12px 12px 2px rgba(0, 0, 255, .2)'
        el.style.borderColor = '#B5BAD0'
        el.style.borderWidth = 4
        a.appendChild(el)
        return el
      }

      const a = createElement('Flex')
      a.style.flexWrap = 'wrap'
      a.style.width = 500
      canvas.appendChild(a)

      addCat(
        'data:image/jpeg;base64,',
      )

      const cat2 = addCat(
        'data:image/jpeg;base64,',
        null,
        200,
        200,
      )
      cat2.style.borderRadius = 16

      const cat3 = addCat(
        'data:image/jpeg;base64,',
        null,
      )
      cat3.addEventListener('pointerdown', () => {
        cat3.src = ''
      })
      cat3.style.borderRadius = 16

    }, 100)

  }, [canvasElRef])

  return (
    <>
      <canvas style={{ backgroundColor: '#ffffff', width: '500px', height: '500px' }} ref={canvasElRef}></canvas>
    </>
  )
}

RenderImageTest.storyName = 'RenderImage'

export default {
  title: 'core/rendering',
  component: RenderImageTest,
  decorators: [(Story: Story) => <div style={{ backgroundColor: '#efefef', width: '100%', height: '100vh' }}><Story /></div>],
}
